INSERT INTO fish_type (name, description)
VALUES
    ('KOHAKU', '“Đỏ và Trắng” là giống phổ biến nhất, với những họa tiết màu đỏ nổi bật trên nền trắng tạo nên một sự tương phản tuyệt đẹp. Sự kết hợp màu sắc đơn giản giữa đỏ và trắng mang lại một chiều sâu cảm nhận. Bị thu hút bởi màu sắc đỏ và trắng, những người yêu thích Koi thường nói rằng “Nuôi Koi bắt đầu và kết thúc với Kohaku.”'),
    ('AIGOROMO', 'Được gọi là Aigoromo (áo choàng chàm), ám chỉ Koromo có những đường vân màu xanh lam bên trong vảy đỏ. Aigoromo được phát triển bằng cách lai giống giữa Kohaku và loài gốc từng được gọi là Goshiki Asagi. Những đường vân được sắp xếp đều đặn, màu sắc sâu và đỏ đậm được đánh giá cao hơn.'),
    ('GINRIN KOHAKU', 'Một biến thể của nền đỏ-trắng. Được phủ lên bởi những vảy bạc. Ngoài những vảy bạc phủ lên sự tinh tế ban đầu của màu đỏ và trắng, nó trông lộng lẫy hơn nhiều và rất được ưa chuộng.'),
    ('TAISHO SANSHOKU', 'Những vệt màu đen than được thêm vào các họa tiết đỏ và trắng cơ bản của Kohaku. Trước đây, nó được phân loại là "Sanke", vì vậy nó vẫn thường được gọi là "Sanke", nhưng được viết là "Sanshoku". Nói đơn giản, loại cá có họa tiết đỏ và đen than trên nền trắng được gọi là Taisho Sanshoku (đỏ, trắng và đen). Rất lộng lẫy.'),
    ('BUDOGOROMO', 'Loại có những họa tiết màu nâu đất được gọi là Budoukoromo (áo choàng nho) gần đây đã trở nên phổ biến hơn.'),
    ('GINRIN SHOWA SANSHOKU', 'Một biến thể của màu cơ bản của Showa (đỏ, trắng, đen). Được phủ lên bởi những vảy bạc. Với một lớp vảy bạc bổ sung, nó trông vui tươi hơn nhiều so với chính Showa Sanshoku.'),
    ('SHOWA SANSHOKU', 'Showa Sanshoku, có nhiều màu đen than và ít màu trắng hơn so với Taisho Sanshoku. Đặc điểm rất năng động và sang trọng. Trải qua nhiều thay đổi về ngoại hình từ lúc nhỏ đến khoảng 5 tuổi. Sau đó, những thay đổi ít hơn có thể tiếp theo. Sự thay đổi về ngoại hình này là đặc điểm lớn nhất của Showa Sanshoku.'),
    ('KUMONRYU', 'Kumonryu, phát triển bằng cách lai giống giữa Doitsukoi (cá chép Đức) và dòng Ugoi, có nền màu đen tuyền, giống như Ugoi. Bị lột trắng ở đây và đó trên đầu và lưng, trông giống như những họa tiết trắng. Các họa tiết màu đen thay đổi và không ổn định, thay đổi theo từng mùa.'),
    ('GINRIN TAISHO SANSHOKU', 'Vảy bạc trên Taisho Sanshoku (đỏ, trắng, đen) tăng thêm vẻ lộng lẫy, giống như ở Showa Sanshoku.'),
    ('TANCHO', 'Koi có một mảng màu đỏ tròn trên đầu thường được gọi là Tancho. Loại tốt nhất là loại có mào đỏ hình tròn và không chạm mắt. Mảng màu đỏ tròn này gợi nhớ đến quốc kỳ Nhật Bản và ngày càng phổ biến ở nước ngoài.'),
    ('GINRIN SHIROUTSURI', 'Ginrin Shiroutsuri là một loại cá được phủ vảy bạc trên những họa tiết trắng Shiroutsuri trên nền đen than.');
INSERT INTO farm (name, location, description, created_date, contact_info)
VALUES
    ('Kokugyokan Koi Farm', 'Ojiya, Niigata', 'Chủ trại cá Kokugyokan Koi Farm hiện tại là ông Seiji Hiroi. Ông Hiroi bắt đầu sự nghiệp nuôi cá Koi từ năm 1975, ngay sau khi tốt nghiệp Trung học. Người sáng lập ra Kokugyokan Koi Farm là cha của ông Hiroi. Ông Hiroi cùng Kokugyokan Koi Farm đã thường xuyên chiến thắng tại All Niigata Koi Show hàng năm. Ông ấy được đánh giá là người lai tạo có mắt nhìn hàng đầu về cá Koi tại Niigata. Dòng cá Kohaku được nuôi dưỡng vừa có màu sắc đẹp vừa có thân hình chuẩn mực đã tạo nên danh tiếng cho trang trại cá này. Các dòng cá nổi bật: Kohaku, Showa, Sanke.', '2024-10-24', NULL),
    ('Ozumi Ikarashi Koi Farm', 'Ozumi, thành phố Nagaoka', 'Ozumi Ikarashi Koi Farm là một trong các trại cá Koi Nhật Bản nổi tiếng và lớn nhất với diện tích 120.000m2. Trang trại Ozumi Ikarashi được thành lập hơn 50 năm về trước. Chủ hiện tại của trang trại Ozumi là 2 anh em Hidetoshi Ikarashi và Kazuhiko Ikarashi. Trang trại cá Koi Ozumi có các ao bùn tự nhiên đạt tiêu chuẩn cao cho ra đời dòng cá Koi chất lượng tốt nhất.', '2024-10-24', 'https://vi-vn.facebook.com/ozumiikarashi/'),
    ('Hasegawa Koi Farm', 'Ojiya, Niigata', 'Hasegawa Koi Farm là một trong các trại cá Koi Nhật Bản nổi tiếng ở Ojiya. Diện tích của trang trại này khá nhỏ so với các trang trại khác, diện tích 15.000m2. Ông chủ của trang trại Hasegawa là ông Hasegawa Mitsuo. Tuy quy mô trại cá không lớn nhưng bạn sẽ phải ngạc nhiên về dòng cá Kohaku của Hasegawa.', '2024-10-24', NULL),
    ('Ikarashi Koi Farm', '1165 Yamakoshimushigame, Nagaoka, Niigata 947-0205', 'Trang trại cá Koi Ikarashi được thành lập vào năm 1962 bởi ông Kazuto Ikarashi. Ông tạo được danh tiếng bởi dòng cá Gosanke mà ông phát triển. Hiện tại, con trai của ông Kazuto Ikarashi – Toshinobu Ikarashi là người tiếp quản và tiếp tục phát triển sự nghiệp kinh doanh cá Koi.', '2024-10-24', NULL),
    ('Matsuda Koi Farm', '245-1 Yokomakuramachi, Nagaoka City, Niigata', 'Một trong các trại cá Koi Nhật Bản nổi tiếng có truyền thống hơn 100 năm trong nghề nuôi cá Koi, đó chính là cái tên Matsuda Koi Farm. Trang trại cá Matsuda không quá lớn như trại cá khác, nhưng đến với Matsuda Koi Farm, bạn sẽ lựa chọn được cho mình cá Koi tốt nhất trong số 30,000 cá Koi Tosai và 10,000 con 2 năm tuổi. Trang trại cá Koi Matsuda cung cấp chủ yếu dòng cá Kohaku, Showa, Sanke. Chất lượng cá Koi được kiểm soát nghiêm ngặt sao cho tốt nhất, thỏa mãn đam mê cá Koi của bất kỳ ai.', '2024-10-24', 'https://vi-vn.facebook.com/MatsudaKoiFarm'),
    ('Oishi Fish Farm', '1606, Kanemaru, Thị trấn Shinichi, Fukuyama, Hiroshima,729-3111', 'Oishi Fish Farm tọa lạc tại Hiroshima, nơi có khung cảnh thiên nhiên thơ mộng, những ngọn núi hùng vĩ tuyệt đẹp. Vì thế mà khí hậu ở đây trong lành, thoáng mát, nguồn nước sạch rất thích hợp để nuôi dưỡng cá Koi. Nổi bật nhất của trang trại cá Koi Oishi là dòng cá nhà Gosanke với điều kiện nuôi dưỡng, chăm sóc và kỹ thuật lai tạo tuyệt vời.', '2024-10-24', 'https://www.facebook.com/oishifishfarm/'),
    ('Taniguchi Koi Farm', '1645, Kanemaru, Shinichicho, Hukuyamashi, Hiroshima 729-3111', 'Taniguchi Koi Farm có quy mô nhỏ hơn so với các trang trại khác ở khu vực Hiroshima. Thế nhưng cá Koi của trang trại được bán dưới dạng Tosai khắp nơi trên toàn thế giới. Chủ trang trại Taniguchi là anh Youchi Taniguchi. Anh Taniguchi là một nhà lai tạo cá Koi trẻ và nổi tiếng ở Hiroshima. Anh đã tạo nên dòng cá Koi Kohaku và Sanke xuất sắc và mang lại nhiều giải thưởng cho trang trại cá Taniguchi.', '2024-10-24', NULL),
    ('Okawa Nishikigoi Center', '92 Ogawa City, Fukuoka Prefecture, Kamishragaki', 'Nhắc đến trang trại cá Okawa, những người yêu thích cá Koi không thể không biết đến lịch sử đặc biệt của chú cá Koi “Okawa Sensuke Kohaku”. Cá Koi “Okawa Sensuke Kohaku” là một trong những loài cá chép đẹp nhất thế giới. Chủ trang trại Okawa, ông Toshiaki Tanaka nhận được cá Koi Sensuke Kohaku từ trại cá Miyatora Youri ở Niigata năm 1964.', '2024-10-24', 'https://www7a.biglobe.ne.jp/~sensuke/entrance.htm'),
    ('Maruchiku Koi Farm', '256-4, Tanushimarumachi Noda, Kurume-shi, Fukuoka, 839-1231', 'Trang trại cá Maruchiku được ông Kenji Konishi thành lập vào năm 1982. Tuy rằng trang trại Maruchiku không có quy mô lớn như những trang trại khác nhưng dưới tài năng và kinh nghiệm của ông Konishi đã lai tạo những Koi dòng Gosanke từ trung bình đến cao cấp.', '2024-10-24', 'https://ja-jp.facebook.com/maruchiku.koi.farm/'),
    ('Oyama Koi Farm', '905-2 Tsukuchi, Sera-cho, Sera-gun, Hiroshima', 'Oyama Koi Farm có hơn 24 năm kinh nghiệm lai tạo các dòng cá Koi chất lượng cả hình dáng lẫn màu sắc. Trại cá ngày càng tạo dựng được tên tuổi của mình tại Hiroshima. Trại cá Koi Oyama cung cấp số lượng cá Koi với chất lượng cao, có thể thích nghi được với môi trường khác nhau đặc biệt là thời tiết nhiệt đới bất thường ở Việt Nam.', '2024-10-24', '0847-27-1176');
