INSERT INTO tours (name, description, start_date, end_date, price, created_date, max_participants, farm_id)
VALUES
    ('Tour thăm trại Kokugyokan Koi', 'Khám phá trang trại Kokugyokan Koi nổi tiếng với dòng cá Kohaku, Showa, Sanke. Tham gia vào quá trình chăm sóc và nuôi dưỡng cá Koi cùng ông Seiji Hiroi.', '2024-11-01', '2024-11-07', 500000, '2024-10-24', 10, 1),
    ('Tour trải nghiệm Ozumi Ikarashi Koi', 'Tham quan trại Ozumi Ikarashi Koi, nơi sản xuất các dòng cá Koi chất lượng cao trong ao bùn tự nhiên. Hãy tận hưởng không gian và tìm hiểu quy trình nuôi cá Koi.', '2024-11-05', '2024-11-12', 600000, '2024-10-24', 15, 2),
    ('Hành trình đến Hasegawa Koi Farm', 'Một chuyến đi đến Hasegawa Koi Farm để xem quy trình nuôi cá Kohaku và tận hưởng cảnh đẹp tại Ojiya.', '2024-11-10', '2024-11-15', 450000, '2024-10-24', 12, 3),
    ('Khám phá Ikarashi Koi Farm', 'Tham quan trang trại Ikarashi và tìm hiểu về dòng cá Gosanke nổi tiếng mà ông Kazuto Ikarashi đã phát triển.', '2024-11-15', '2024-11-20', 550000, '2024-10-24', 10, 4),
